    <script type="text/javascript">jQuery(document).ready(function(){ runprettyPhoto(); }); function runprettyPhoto(){ //=================================== PRETTYPHOTO ===================================// jQuery('a[data-rel]').each(function() {jQuery(this).attr('rel', jQuery(this).data('rel'));}); jQuery("a[rel^='prettyPhoto']").prettyPhoto({ animationSpeed:'slow', theme:'pp_default', /* light_rounded / dark_rounded / light_square / dark_square / facebook */ gallery_markup:'', social_tools: false, slideshow:2000 }); }</script>
    <script type="text/javascript">function runquicksand(){ // get the action filter option item on page load var $filterType = jQuery('#filter li.current a').attr('class'); var $holder = jQuery('.portfoliolist'); var $data = $holder.clone(); jQuery('#filter li a').click(function(e) { jQuery('#filter li').removeClass('current'); var $filterType = jQuery(this).data('filter'); jQuery(this).parent().addClass('current'); if ($filterType == '*') { var $filteredData = $data.find('div.item'); } else { var $filteredData = $data.find('div.item.' + $filterType ); } $holder.quicksand($filteredData, { easing: 'easeOutQuart', adjustWidth: 'auto', enhancement: function(){ runprettyPhoto(); jQuery('.klasik-pf-img a').hover( function() { jQuery(this).find('.rollover').stop().fadeTo(500, 0.6); }, function() { jQuery(this).find('.rollover').stop().fadeTo(500, 0); } ); } }); return false; }); } jQuery(window).load(function(){ runquicksand(); });</script>
